const{contextBridge,ipcRenderer}=require("electron");contextBridge.exposeInMainWorld("electronAPI",{auth:{login:()=>ipcRenderer.invoke("auth:login"),logout:()=>ipcRenderer.invoke("auth:logout"),getToken:()=>ipcRenderer.invoke("auth:getToken"),getCurrentUser:()=>ipcRenderer.invoke("auth:getCurrentUser")},graph:{query:(e,r,n)=>ipcRenderer.invoke("graph:query",e,r,n)},llm:{chat:e=>ipcRenderer.invoke("llm:chat",e),isAvailable:()=>ipcRenderer.invoke("llm:isAvailable")},mcp:{call:(e,r,n)=>ipcRenderer.invoke("mcp:call",e,r,n),listServers:()=>ipcRenderer.invoke("mcp:listServers")},config:{get:()=>ipcRenderer.invoke("config:get"),update:e=>ipcRenderer.invoke("config:update",e)},on:(e,r)=>{["auth-status-changed","chat-message","graph-api-call"].includes(e)&&ipcRenderer.on(e,r)},removeAllListeners:e=>{["auth-status-changed","chat-message","graph-api-call"].includes(e)&&ipcRenderer.removeAllListeners(e)}});